import { Pipe, PipeTransform } from "@angular/core";

@Pipe({
    name: 'formattedDate',
    standalone: true,
})

export class FormattedDate implements PipeTransform {
      ending(n: number) {
          switch (n) {
              case 1:
                  return 'st';
              case 2:
                  return 'nd';
              case 3:
                  return 'rd';
              default: 
                  return 'th'
            }
        }
    transform(value: any) {
        const date = new Date(value);
        let months = ["January", "February", "March", "April", "May", "June", "July",
            "August", "September", "October", "November", "December"];
        return `${months[date.getMonth()]} ${date.getDate()}${this.ending(date.getDate())}, ${date.getFullYear()}`;
    }

}
