import { Pipe, PipeTransform } from "@angular/core";

@Pipe({
    name: "cropped",
     standalone: true,
})

export class CroppedTextPipe implements PipeTransform {
    transform(value: string) {
        return value.length >= 50 ? `${value.slice(0, 50)}...` : value;
    }

}
